import './App.css';
import React, { Fragment, useState } from 'react';
import RowName from "./component/RowName";
import RowDate from "./component/RowDate";
import RowContext from "./component/RowContext";
import SendBtn from "./component/SendBtn";

function getName(childName) {
  this.setName({ name: childName });
}

function getDate(childDate) {
  this.setDate({ date: childDate })
}

function App() {
  var [name, setName] = useState("");
  var [date, setDate] = useState("");
  var [context, setContext] = useState("");

  const getName = ({ target: { value } }) => setContext(value)
  const getDate = ({ target: { value } }) => setContext(value)
  const getContext = ({ target: { value } }) => setContext(value)

  return (
    <Fragment>
      <div className="mainGrid">
        <form className="inputForm">
          <div className="rowNameDate">
            <RowName
              value={name}
              onChange={getName} />
            <RowDate value={date}
              onChange={getDate} />
          </div>
          <RowContext
            value={context}
            onChange={getContext} />
        </form>
        <SendBtn />
      </div>
      {name}, {date}, {context}
    </Fragment>
  );
}

export default App;
